styles.css:
.gg-code-editor {
    color: #d8dee9;
    padding: 20px;
    border-radius: 5px;
    font-family: 'Source Code Pro', monospace;
    overflow-x: auto;
    width: 80%;
    max-width: 800px;
    margin: 0 auto;
}

.gg-code-editor code {
    display: block;
    white-space: pre-wrap;
    word-break: break-word;
}

.gg-code-editor .gg-line-number {
    display: inline-block;
    width: 30px;
    text-align: right;
    margin-right: 10px;
    color: #4c566a;
    user-select: none;
}

.gg-code-editor .gg-keyword {
    color: #81a1c1;
}

.gg-code-editor .gg-string {
    color: #a3be8c;
}

.gg-code-editor .gg-comment {
    color: #616e88;
    font-style: italic;
}

.gg-code-editor .gg-function {
    color: #8fbcbb;
}

.gg-code-editor .gg-variable {
    color: #d08770;
}



.tech-stack-marquee {
    overflow: hidden;
    white-space: nowrap;
    width: 80%;
}

.marquee-content {
    display: grid;
    grid-template-rows: repeat(4, 1fr); /* Adjust the number of rows */
    animation: marquee 20s linear infinite;
}

@keyframes marquee {
    0% {
        transform: translateY(0%);
    }
    100% {
        transform: translateY(-50%); /* Adjust based on the number of rows */
    }
}

.marquee-row {
    display: flex;
    justify-content: space-around;
}

.tech-item {
    display: inline-block;
    padding: 0 20px;
    font-size: 1.2em;
    color: #88c0d0;
}



.gg-p-highlight {
  color: #5553C5;
}
